        this.errMessage = "";
        if(this.state.retNbr < 1 || this.state.retNbr > 20) {
           this.errMessage = "Returned Results must lie between 1 and 20.";
        }
        this.setState({err: this.errMessage});
        return this.errMessage;
    }


    handleSubmit = question => { 
        this.setState({err: ""});
        const errMessage = this.validateState()
        if(errMessage !== "") {

            return;
        }

        //this.setState({ questions: [...this.state.questions,question] }, () => {
        if (question !== null) {
            fetch("http://139.19.51.11:5000/getanswer", {
