                    //})
              }));
          }

       // });

    }


    handleClearConv = () => {
        console.log("in clear conv")
        this.setState(this.initialState)
    }



    render() {

       //const {questions, answers, retnbr, indriretnbr, nodethreshold, edgethreshold, indriscore, nodescore, edgescore} = this.state;

       //if(this.state.questions.length > 0 && this.state.questions.length === this.state.answers.length ) {
        //if(this.state.turn !== -1) {
