        var errorStyle = 
                {color:"#FF0000"}
        return (

          //  <div className="container">
           //     <Form handleSubmit={this.handleSubmit} onSubmit={this.handleSubmit} handleClearConv={this.handleClearConv} />      
          //      <TableBody questions={this.state.questions} answers={this.state.answers}/>
        // <div>toast.err({this.state.err}) </div>    
            <div className="flex-row">

                {this.state.err && <div className="error-message" style={errorStyle}>{this.state.err}</div>}
                <div className="flex-large">
                    <Form handleSubmit={this.handleSubmit} onSubmit={this.handleSubmit} handleClearConv={this.handleClearConv} />      
                    <TableBody questions={this.state.questions} answers={this.state.answers}/>

                </div>
                <div className="small-container">
                    <div className="flex-small">
                        <Options onChange={this.handleOptionChange} value={this.state}/>
                    </div>
                </div>
            </div>


          ); 
      // }else {
       ///    console.log("in else")
       //    return (
       //     <div className="container">
       //     <Form handleSubmit={this.handleSubmit} handleClearConv={this.handleClearConv}  /> 
