import React, { Component } from 'react'
import Table from './Table'
import Form from './Form'
import QuestionForm from './QuestionForm'





class App extends Component {


    state = {
        questions:  [],
          answers: [],
          turn: -1
        };

    // prev_questions = []
     //prev_answers = []
    // prev_turn = -1



    handleSubmit = question => { 

        this.setState({ questions: [...this.state.questions,question] }, () => {

            fetch("http://localhost:5000/getanswer", {
                 headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json', credentials: 'same-origin'},

            method: 'post', body: JSON.stringify({"questions" : this.state.questions })
              }).then(response => {
                if (response.ok) {
                  return response.json();
                } else {
                    console.log("ERROR when calling REST Api, response was:" + response);
                  throw new Error('ERROR when calling REST Api');
                  this.setState({ answers:  [...this.state.answers,"ERROR while calling REST API"] }, () => {
                    this.setState({turn: this.state.turn+1});

                    })
                }
              })
              .then(data =>this.setState({ answers:  [...this.state.answers,data] }, () => {
                  this.setState({turn: this.state.turn+1});

              }))
              .catch(error => this.setState({error: error }, () => {
                  console.log("ERROR when calling REST Api: " + error);
                  this.setState({ answers:  [...this.state.answers,"ERROR while calling REST API"] }, () => {
                    this.setState({turn: this.state.turn+1});

                    })
              }));


        });

    }






    render() {

       if(this.state.questions.length > 0 && this.state.questions.length === this.state.answers.length ) {
        //if(this.state.turn !== -1) {
           return (
            <div className="container">
            <Form handleSubmit={this.handleSubmit} />      
            <TableBody questions={this.state.questions} answers={this.state.answers}/>
            </div>
          ); 
       }else {
           return (
            <div className="container">
            <Form handleSubmit={this.handleSubmit} /> 

            </div>
          ); 
       }

    };
}

const TableBody = props  => {

   const {questions, answers} = props;

   console.log("in tablebody, questions: " + questions)
   console.log("in tablebody, answers: " + answers)


    return (
        <table> 
            <tbody>
             {questions.map((question, index) => {
                 return (
                    <tr key={index}>
                        <td>
                        <InnerTable question={question} answers={answers[index]} nbr={index}/>
                        </td>
                    </tr>

                 )})
            }
            </tbody>
        </table>
    );


}; 


const InnerTable = props => {

    const {question, answers, nbr} = props

    const rows = answers.map((answer, index) => {
        return(
            <tr key={"".concat(nbr.toString()).concat("_").concat(index.toString())}>
                <td>Rank {index} : {answer}</td>
            </tr>
         );
    });

    return (
        <table>
           <thead>
                <tr key={question}>
                    <th>Result for: {question}</th>
                </tr>
           </thead>
           <tbody>{rows}</tbody>
        </table>
    )

};
export default App
