import React, { Component } from 'react'
import Table from './Table'
import Form from './Form'
import QuestionForm from './QuestionForm'




class App extends Component {
  state = {
    questions:  []
      ,
      answers: [],
      turn: 0

    };


    handleSubmit = question => {
    this.setState({ questions: [...this.state.questions,question] });

    fetch("http://localhost:5000/getanswer", {
         headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json', credentials: 'same-origin'},

    method: 'post', body: JSON.stringify({"questions" : this.state.questions })
      }).then(response => {
        if (response.ok) {
          return response.json();
        } else {
            console.log("error case:", response);
          throw new Error('Something went wrong ...');
        }
      })
      .then(data => this.setState({ answers:  [...this.state.answers, [data.answers] ]}))
      .catch(error => this.setState({ error }));

      this.setState({turn: this.state.turn+1});
    }



 render() {
     const {questions, answers } = this.state;
     if(this.state.turn !== 0 ){
           return (
        <div className="container">
        <Form handleSubmit={this.handleSubmit} /> 

         <TableBody  question={questions[this.state.turn]} answers={answers[this.state.turn]}/>
        </div>
      );
      }else {
          return (
        <div className="container">
        <Form handleSubmit={this.handleSubmit} /> 

        </div>
      );
      }

  }

};


const TableBody = props  => {



     return (

             <table>
                <thead>
                    <tr>
                        <th>Result for: {props.question}</th>
                    </tr>
                </thead>
                <tbody>{props.answers}</tbody>
            </table>
            );

  };


export default App
