import React, { Component } from 'react'

class Form extends Component {
  constructor(props) {
    super(props)

    this.initialState = {
      question: ''
    };

    this.state = this.initialState
  }

  handleChange = event => {
  const { name, value } = event.target

  this.setState({
    [name]: value

  })
}


submitForm = event => {
  event.preventDefault()
  this.props.handleSubmit(this.state.question)
  this.setState(this.initialState)

}




clearConv = () => {
    this.props.handleClearConv()
}

  render() {
  var inlinestyle = {display: "inline", width: "68%", "margin-right": "10px", "margin-bottom": "50px"}

  return (
    <form onSubmit={this.submitForm} >

      <input
        type="text"
        name="question"
        value={this.state.question}
        onChange={this.handleChange}
        placeholder="Please enter your question"  style={inlinestyle}/>
        {"   "}
        <input type="button" value="Answer" onClick={this.submitForm} />
        {"   "}
        <input type="button" value="Clear"  onClick={this.clearConv} />

    </form>

  );
}
