import React, { Component } from 'react'

import Form from './Form'
import Options from './Options'
import './index.css'





class App extends Component {

 constructor(props) {
    super(props);

    this.initialState = {
        questions:  [],
        answers: [],
        turn: -1,
        retNbr: 3,
        indriRetNbr: 100,
        nodeThreshold: 0.75,
        edgeThreshold: 0.0,
        convquery: "conv_uw",
        h1: 0.6,
        h2: 0.3,
        h3: 0.1,
        err: ""

    };

    this.state = this.initialState;
  }
   // state = {
     //   questions:  [],
       //   answers: [],
          //turn: -1
        //};

    // prev_questions = []
     //prev_answers = []
    // prev_turn = -1

    handleOptionChange = (optionId, value) => {
        this.setState({ [optionId]: value });
    }

    validateState = () => {
        console.log("state in validateState: " + this.state.edgeThreshold)
        var errMessage = "";
        if(this.state.retNbr < 1 || this.state.retNbr > 20) {
           errMessage = errMessage +" The number of returned results must lie between 1 and 20!";
        }
        if(this.state.indriRetNbr < 10 || this.state.indriRetNbr > 1000) {
            errMessage = errMessage +" The number of returned Indri passages must lie between 10 and 1000!";
        }
        if(this.state.nodeThreshold < 0.5 || this.state.nodeThreshold > 1.0) {
            errMessage = errMessage +" The node threshold must lie between 0.5 and 1.0!";
        }
        if(this.state.edgeThreshold < 0 || this.state.edgeThreshold > 0.1) {
            errMessage = errMessage +" The edge threshold must lie between 0.0 and 0.1!";
        }

        var score = parseFloat(this.state.h1) + parseFloat(this.state.h2) + parseFloat(this.state.h3)
        console.log("score: " + score)
        var scoreVariance = 0.0001
        var diff = Math.abs(1.0 - score)
        console.log("diff: " + diff)
        if(Math.abs(1.0 - score) > scoreVariance ) {
            errMessage = errMessage +" The hyperparameters must sum up to 1.0!";
        }


        this.setState({err: errMessage});
        return errMessage;
    }


    handleSubmit = question => { 
        this.setState({err: ""});
        var errMessage = this.validateState();
        if(errMessage !== "") {  
            console.log("Abort because of wrong parameters")
            return;
        }

        //this.setState({ questions: [...this.state.questions,question] }, () => {
        if (question !== null && question !== "") {
            fetch("http://139.19.6.31:5000/getanswer", {
            //fetch("http://139.19.51.11:5000/getanswer", {
          // fetch("http://localhost:5000/getanswer", {
                 headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json', credentials: 'same-origin'},

            method: 'post', body: JSON.stringify({"questions" : [...this.state.questions,question], 
                                                    "retNbr": this.state.retNbr, "indriRetNbr": this.state.indriRetNbr, 
                                                 "nodeThreshold": this.state.nodeThreshold, "edgeThreshold": this.state.edgeThreshold,
                                              "convquery": this.state.convquery, "h1": this.state.h1,
                                                "h2": this.state.h2, "h3": this.state.h3})
              }).then(response => {
                if (response.ok) {
                  return response.json();
                } else {
                    console.log("ERROR when calling REST Api, response was:" + response);
                  //throw new Error('ERROR when calling REST Api');
               //   this.setState({ answers:  [...this.state.answers,"ERROR while calling REST API"] }, () => {
                  //  this.setState({turn: this.state.turn+1});

                  //  })
                }
              })
              .then(data =>this.setState({questions: [...this.state.questions, question],
                   answers:  [...this.state.answers,data], turn: this.state.turn+1 }
               //, () => { this.setState({turn: this.state.turn+1});

              ))
              .catch(error => this.setState({error: error }, () => {
                  console.log("ERROR when calling REST Api: " + error);
                  //this.setState({ answers:  [...this.state.answers,["ERROR while calling REST API"]] }, () => {
                    //this.setState({turn: this.state.turn+1});

                    //})
              }));
          }

       // });

    }


    handleClearConv = () => {
        //this.setState(this.initialState)
        this.setState({questions:  [],answers: [], turn: -1})

    }



    render() {

        var errorStyle = 
                {color:"#FF0000"}

        return (

            <div className="flex-row">
                <div className="flex-large">
                    {this.state.err && <div className="error-message" style={errorStyle}>{" "}{this.state.err}</div>}

                    <Form handleSubmit={this.handleSubmit} onSubmit={this.handleSubmit} handleClearConv={this.handleClearConv} />      
                    <TableBody questions={this.state.questions} answers={this.state.answers}/>
                </div>
                <div className="small-container">
                    <div className="flex-small">
                        <Options onChange={this.handleOptionChange} value={this.state}/>
                    </div>
                </div>
            </div>

          ); 

    };
}



const TableBody = props  => {

   const {questions, answers} = props;

   console.log("in tablebody, questions: " + questions)
   console.log("in tablebody, answers: " + answers)


    return (
        <table> 
            <tbody>
             {[...questions].reverse().map((question, index) => {
                 return (
                    <tr key={index}>
                        <td>
                        <InnerTable question={question} answers={[...answers].reverse()[index]} nbr={index}/>
                        </td>
                    </tr>

                 )})
            }
            </tbody>
        </table>
    );


}; 


const InnerTable = props => {

    const {question, answers, nbr} = props

    const rows = answers.map((answer, index) => {
        return(
            <tr key={"".concat(nbr.toString()).concat("_").concat(index.toString())}>
                <td><b>Rank {index} : </b>{answer}</td>
            </tr>


         );
    });

    return (
        <table>
           <thead>
                <tr key={question}>
                    <th>Result for: {question}</th>
                </tr>
           </thead>
           <tbody>{rows}</tbody>
        </table>
    )

};
export default App
