import React, { Component } from 'react'
import Table from './Table'
import Form from './Form'
import QuestionForm from './QuestionForm'




class App extends Component {

  state = {
    questions:  [],
      answers: [],
      turn: -1
    };


    handleSubmit = question => { 

    this.setState({ questions: [...this.state.questions,question] });

    console.log('After setState')
    console.log('answers> ' + this.state.answers)
    console.log('questions> ' + this.state.questions)
    console.log(this.state.turn)

    if (this.state.questions.length > 0) { 
        var jsonanswer = ["quack", "blub", "hi"];

        fetch("http://localhost:5000/getanswer", {
             headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json', credentials: 'same-origin'},

        method: 'post', body: JSON.stringify({"questions" : this.state.questions })
          }).then(response => {
            if (response.ok) {
              return response.json();
            } else {
                console.log("error case:", response);
              throw new Error('Something went wrong ...');
            }
          })
          .then(data =>this.setState({ answers:  [...this.state.answers,data] }))
          .catch(error => this.setState({ error }));


          this.setState({turn: this.state.turn+1}) ;

          console.log(this.state.answers)
          console.log(this.state.questions)
          console.log(this.state.turn)
       }
    }



 render() {


    return (
        <div className="container">
        <Form handleSubmit={this.handleSubmit} /> 

        </div>
      ); 




};
}

const TableBody = props  => {

    console.log(props.answers)
      const rows = props.answers.map((answer, index) => {
         return(
         <tr key={index}>

                    <td>Rank {index} : {answer}</td>
                </tr>
                );
     });

     return (

             <table>
                <thead>
                    <tr>
                        <th>Result for: {props.question}</th>
                    </tr>
                </thead>
                <tbody>{rows}</tbody>
            </table>
            );

  };


export default App
