import React, { Component } from 'react'
import Table from './Table'
import Form from './Form'
import QuestionForm from './QuestionForm'
import './index.css'





class App extends Component {

 constructor(props) {
    super(props);

    this.initialState = {
        questions:  [],
        answers: [],
        turn: -1
    };

    this.state = this.initialState;
  }
   // state = {
     //   questions:  [],
       //   answers: [],
          //turn: -1
        //};

    // prev_questions = []
     //prev_answers = []
    // prev_turn = -1



    handleSubmit = question => { 

        //this.setState({ questions: [...this.state.questions,question] }, () => {
        if (question !== null) {
            fetch("http://139.19.51.11:5000/getanswer", {
          // fetch("http://localhost:5000/getanswer", {
                 headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json', credentials: 'same-origin'},

            method: 'post', body: JSON.stringify({"questions" : [...this.state.questions,question] })
              }).then(response => {
                if (response.ok) {
                  return response.json();
                } else {
                    console.log("ERROR when calling REST Api, response was:" + response);
                  //throw new Error('ERROR when calling REST Api');
               //   this.setState({ answers:  [...this.state.answers,"ERROR while calling REST API"] }, () => {
                  //  this.setState({turn: this.state.turn+1});

                  //  })
                }
              })
              .then(data =>this.setState({questions: [...this.state.questions, question],
                   answers:  [...this.state.answers,data], turn: this.state.turn+1 }
               //, () => { this.setState({turn: this.state.turn+1});

              ))
              .catch(error => this.setState({error: error }, () => {
                  console.log("ERROR when calling REST Api: " + error);
                  //this.setState({ answers:  [...this.state.answers,["ERROR while calling REST API"]] }, () => {
                    //this.setState({turn: this.state.turn+1});

                    //})
              }));
          }

       // });

    }


    handleClearConv = () => {
        console.log("in clear conv")
        this.setState(this.initialState)
    }



    render() {

      const {questions, answers, retnbr} = this.state;

       //if(this.state.questions.length > 0 && this.state.questions.length === this.state.answers.length ) {
        //if(this.state.turn !== -1) {
         console.log("in if")
           return (

          //  <div className="container">
           //     <Form handleSubmit={this.handleSubmit} onSubmit={this.handleSubmit} handleClearConv={this.handleClearConv} />      
          //      <TableBody questions={this.state.questions} answers={this.state.answers}/>
            <div className="flex-row">
                <div className="flex-large">
                    <Form handleSubmit={this.handleSubmit} onSubmit={this.handleSubmit} handleClearConv={this.handleClearConv} />      
                    <TableBody questions={this.state.questions} answers={this.state.answers}/>

                 </div>
                <div className="flex-small">
                <label for="retnbr">Return Number: [1,...,20]</label>
                <input type="text" name="retnbr" placeholder="3" value={retnbr}/>
                </div>
            </div>


          ); 
      // }else {
       ///    console.log("in else")
       //    return (
       //     <div className="container">
       //     <Form handleSubmit={this.handleSubmit} handleClearConv={this.handleClearConv}  /> 
       //     <TableBody questions={questions.splice(0, questions.length-1)} answers={this.state.answers}/>
       //     </div>
       //   ); 
       //}
    //   return (
    //   <div class="flex-row">
    //       <div class="flex-small"></div>
        //    <div class="flex-small"></div>
       // </div>

     // );
    };
}



const TableBody = props  => {

   const {questions, answers} = props;

   console.log("in tablebody, questions: " + questions)
   console.log("in tablebody, answers: " + answers)


    return (
        <table> 
            <tbody>
             {[...questions].reverse().map((question, index) => {
                 return (
                    <tr key={index}>
                        <td>
                        <InnerTable question={question} answers={[...answers].reverse()[index]} nbr={index}/>
                        </td>
                    </tr>

                 )})
            }
            </tbody>
        </table>
    );


}; 


const InnerTable = props => {

    const {question, answers, nbr} = props

    const rows = answers.map((answer, index) => {
        return(
            <tr key={"".concat(nbr.toString()).concat("_").concat(index.toString())}>
                <td><b>Rank {index} : </b>{answer}</td>
            </tr>


         );
    });

    return (
        <table>
           <thead>
                <tr key={question}>
                    <th>Result for: {question}</th>
                </tr>
           </thead>
           <tbody>{rows}</tbody>
        </table>
    )

};
export default App
