import React, { Component } from 'react'

class Form extends Component {
  constructor(props) {
    super(props)

    this.initialState = {
      question: ''
    };

    this.state = this.initialState
  }

  handleChange = event => {
  const { name, value } = event.target

  this.setState({
    [name]: value

  })
}


submitForm = event => {
  event.preventDefault()
  this.props.handleSubmit(this.state.question)
  this.setState(this.initialState)

}

handleKeyUp(event) {
  if (event.keyCode === 13) return this.submitForm()
}


clearConv = () => {
    this.props.handleClearConv()
}

  render() {
  const { q } = this.state;

  return (
    <form onSubmit={this.submitForm}>
      <label>Please enter your question</label>
      <input
        type="text"
        name="question"
        value={q}
        onChange={this.handleChange}
        />
        <input type="button" value="Answer" placeholder='Enter your question...' onClick={this.submitForm} />
        {"   "}
        <input type="button" value="Clear Conversation" onClick={this.clearConv} />
    </form>
  );
}


}



export default Form;
