import React, { Component } from 'react'

import Form from './Form'
import Options from './Options'
import './index.css'
import Loader from 'react-loader-spinner';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import ReactHtmlParser from 'react-html-parser';

//current blue button color: #0366EE

class App extends Component {

 constructor(props) {
    super(props);

    this.initialState = {
        questions:  [],
        answers: [],
        answerids: [],
        nodes: [],
        edges:[],
        topsentences: [],
        turn: -1,
        retNbr: 3,
        indriRetNbr: 100,
        nodeThreshold: 0.75,
        edgeThreshold: 0.0,
        convquery: "conv_uw",
        h1: 0.5,
        h2: 0.3,
        h3: 0.1,
        h4: 0.1,
        err: "",
        api_err: ""

    };

    this.state = this.initialState;
  }

    handleOptionChange = (optionId, value) => {
        this.setState({ [optionId]: value });
    }

    validateState = () => {

        var errMessage = "";
        if(this.state.retNbr < 1 || this.state.retNbr > 20) {
           errMessage = errMessage +" The number of returned results must lie between 1 and 20!";
        }
        if(this.state.indriRetNbr < 10 || this.state.indriRetNbr > 1000) {
            errMessage = errMessage +" The number of returned Indri passages must lie between 10 and 1000!";
        }
        if(this.state.nodeThreshold < 0.5 || this.state.nodeThreshold > 1.0) {
            errMessage = errMessage +" The node threshold must lie between 0.5 and 1.0!";
        }
        if(this.state.edgeThreshold < 0 || this.state.edgeThreshold > 0.1) {
            errMessage = errMessage +" The edge threshold must lie between 0.0 and 0.1!";
        }

        var score = parseFloat(this.state.h1) + parseFloat(this.state.h2) + parseFloat(this.state.h3) + parseFloat(this.state.h4)

        var scoreVariance = 0.0001

        if(Math.abs(1.0 - score) > scoreVariance ) {
            errMessage = errMessage +" The hyperparameters must sum up to 1.0!";
        }

        this.setState({err: errMessage});
        return errMessage;
    }


    handleSubmit = question => { 
        this.setState({err: "", api_err: ""});
        var errMessage = this.validateState();
        if(errMessage !== "") {  
            console.log("Abort because of wrong parameters")
            return;
        }

        if (question !== null && question !== "") {
            trackPromise(      
                //fetch("http://139.19.6.31:5000/getanswer", {
                fetch("http://139.19.51.11:9999/getanswer", {
                //fetch("http://crown.mpi-inf.mpg.de:5000/getanswer", {
                // fetch("http://139.19.52.26:5000/getanswer", {
                     headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json', credentials: 'same-origin'},

                method: 'post', body: JSON.stringify({"questions" : [...this.state.questions,question], 
                                                        "retNbr": this.state.retNbr, "indriRetNbr": this.state.indriRetNbr, 
                                                     "nodeThreshold": this.state.nodeThreshold, "edgeThreshold": this.state.edgeThreshold,
                                                  "convquery": this.state.convquery, "h1": this.state.h1,
                                                    "h2": this.state.h2, "h3": this.state.h3, "h4": this.state.h4})
                  }).then(response => {
                    if (response.ok) {
                      return response.json();
                    } else {
                        console.log("ERROR when calling REST Api, response was:" + response);                   
                    }
                  })
                  .then(data =>this.setState({questions: [...this.state.questions, question],
                       answers:  [...this.state.answers,data["paragraphs"]], answerids:  [...this.state.answerids,data["ids"]], 
                       nodes: [...this.state.nodes, data["nodes"]], edges: [...this.state.edges, data["edges"]], turn: this.state.turn+1, topsentences:  [...this.state.topsentences,data["top_sentences"]] }

                  ))
                  .catch(error => this.setState({api_err: error }, () => {
                      console.log("ERROR when calling REST Api: " + error);

                })));
        }

    }


    handleClearConv = () => {    
        this.setState({questions:  [],answers: [], answerids: [], nodes: [], edges: [], turn: -1, topsentences: []})

    }


    showSampleAnswers = () => {
        var quest = ["What flowering plants work for cold climates?", "How much cold can pansies tolerate?", "Does it have different varieties?", "Can it survive frost?", "How do I protect my plants from cold weather?"  ]
        var aw1 = ["mosses and shrubs. Plant life on the tundra consists of short clumps of liches, mosses, shrubs and surprisingly, several hundred species of flowering plants. These plants are all adapted to cold, windy climates, and have shallow root systems to take advantage of the thin layer of thawed soil above the permafrost.osses and shrubs. Plant life on the tundra consists of short clumps of liches, mosses, shrubs and surprisingly, several hundred species of flowering plants. These plants are all adapted to cold, windy climates, and have shallow root systems to take advantage of the thin layer of thawed soil above the permafrost.",
        "Back to Top. Hardy flowering plants can be defined as seasonal plants or a plant, which is adapted to the temperature, climate and to certain area. Hardy flowering plants have an ability to tolerate extreme cold, heat, wind, floods, and other geographic location, etc. Different types of hardy flowering plants.",
        "The racemes of lipstick-red buds open to orange flowers; there's also red in the stems of the leaves. This tree's ability to flower only months after germination, quickness of growth, and poisonous seeds make it a weed in climates where it's hardy. The common name, rattlebox, comes from the dried seed pods.They hang on the branches in the winter, and the seeds they contain rattle in the wind. There's no danger when growing Sesbania tripetii in climates cold enough that the plants or (scarce) seeds can't survive the winter. new raceme emerges from the base of each new leaf. its eagerness to begin flowering: S. tripetii can begin flowering two or three months from germination, so the tree can also be grown as a colorful annual. Flowering season. The plant remains in bloom as long as it is in active growth, and that can be months."]
        var aw2 = [" Also known as Winter Pansies, these are hardier than regular bedding pansies, tolerating more winter cold and summer heat. Plants form a low, bushy mound of bright green leaves, with loads of fragrant miniature pansy flowers from early spring to late fall, all winter in milder regions.lso known as Winter Pansies, these are hardier than regular bedding pansies, tolerating more winter cold and summer heat. Plants form a low, bushy mound of bright green leaves, with loads of fragrant miniature pansy flowers from early spring to late fall, all winter in milder regions.",
        "Back to Top. Hardy flowering plants can be defined as seasonal plants or a plant, which is adapted to the temperature, climate and to certain area. Hardy flowering plants have an ability to tolerate extreme cold, heat, wind, floods, and other geographic location, etc. Different types of hardy flowering plants.",
        "Some of the types of flowers that are considered hardy annuals include the pansy, cornflower, and larkspur. Annuals that can tolerate damp, cold temperatures, but not frost are called half-hardy annuals.Some examples of half-hardy annual flowers include forget-me-nots, bells of Ireland, and baby's breath.hese terms are used as a means of identifying and separating certain types of flowers. What makes them different from one another is the life cycle of the plants and how long they produce blooms. When planting flowers or creating a garden, it is necessary to understand which plants are perennials and which are annuals."]
        var aw3 = ["Also known as Winter Pansies, these are hardier than regular bedding pansies, tolerating more winter cold and summer heat. Plants form a low, bushy mound of bright green leaves, with loads of fragrant miniature pansy flowers from early spring to late fall, all winter in milder regions.lso known as Winter Pansies, these are hardier than regular bedding pansies, tolerating more winter cold and summer heat. Plants form a low, bushy mound of bright green leaves, with loads of fragrant miniature pansy flowers from early spring to late fall, all winter in milder regions. ", 
        "Common annuals are marigold, vinca, begonia, coleus, zinnia, impatiens, petunia, nasturtium and pentas. But climate plays a big role in determing an annual plant's lifecycle â some varieties of daisies, geranium, lantana, mandevilla, pansies and verbena are perennials in warm climates.nnuals. As their name implies, annuals are plants that complete their life cycle in only one season. Typically used to add seasonal color to flowerbeds and planters, these prolific bloomers die back after flowering. Removing spent blooms will stimulate annuals to produce more showy flowers. ", 
        "Back to Top. Hardy flowering plants can be defined as seasonal plants or a plant, which is adapted to the temperature, climate and to certain area. Hardy flowering plants have an ability to tolerate extreme cold, heat, wind, floods, and other geographic location, etc. Different types of hardy flowering plants."]
        var aw4 = ["The plant's growth is highly variable due to a high complexity of different subspecies, varieties and landraces (domesticated plants or animals adapted to the environment in which they originated). However, in general it is undemanding and altitude-hardy. It is grown from coastal regions to over in the Andes near the equator, with most of the cultivars being grown between and . Depending on the variety, optimal growing conditions are in cool climates with temperatures that vary between during the night to near during the day. Some cultivars can withstand lower temperatures without damage. Light frosts normally do not affect the plants at any stage of development, except during flowering. Mid-summer frosts during flowering, often occurring in the Andes, lead to sterilization of the pollen. Rainfall conditions are highly variable between the different cultivars, ranging from during growing season. Growth is optimal with well-distributed rainfall during early growth and development and dry conditions during seed maturation and harvesting.",
        "Penta (Pentas lanceolata) is an evergreen bush that, in warm climates, will light up the landscape all year with colorful star-shaped clusters. Hummingbirds and butterflies will often visit the penta blooms before they visit other flowering plants. Although penta can survive an occasional light frost, it won't tolerate a hard freeze. If you live in a cold climate, either grow penta as an annual or plant it in a container and bring it indoors for the winter.",
        "Overview. Perfect for gardens in temperate climates, hydrangeas need a winter dormancy that is punctuated with frost and subfreezing temperatures. However, if a hydrangea shrub is leafy or in bloom, frost will scald it and turn the plant tissue brown, and the petals and leaves will drop off.Untimely spring frosts can kill flower and leaf buds on shrubs, and reduce or prevent flowering later in spring and summer.rotect shrubs from a late frost by covering them with a cloth sheet or blanket when unseasonal cold is forecast. Plants nipped by frost should be cleaned and pruned only when there is no further threat of frost in spring. Frost causes hydrangeas' petals and leaves to turn brown in early to mid-autumn."]
        var aw5 = ["To protect your plants adequately, you need know what plants you have, and to understand: our desert cold weather; how plants respond to cold; some methods to prevent severe frost damage; and what to do if a plant is frost damaged.Understanding our Desert Cold Weather Humidity slows temperature changes.o protect your plants adequately, you need know what plants you have, and to understand: our desert cold weather; how plants respond to cold; some methods to prevent severe frost damage; and what to do if a plant is frost damaged.",
