import React, { Component } from 'react'
import Table from './Table'
import Form from './Form'
import QuestionForm from './QuestionForm'




class App extends Component {
  state = {
    questions: [ "this is a test question"
      ],
      answers: []

    };


    handleSubmit = question => {
    this.setState({ questions: [...this.state.questions, question] });
    }

componentDidMount() {
    console.log("in mount");
    window.fetch('127.0.0.1:5000/getanswer', {
    method: 'post', body: JSON.stringify({"questions" : this.state.questions })
      }).then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Something went wrong ...');
        }
      })
      .then(data => this.setState({ answers: data.answers }))
      .catch(error => this.setState({ error }));
  }


 render() {
     const {questions, answers } = this.state;
      return (
        <div className="container">
        <Form handleSubmit={this.handleSubmit} /> 
         <TableBody  questions={questions} answers={answers}/>

        </div>
      );
  }

};


const TableBody = props  => {
     const rows = props.answers.map((answer, index) => {
         return(
         <tr key={index}>
                    <td>{props.questions[0]}</td>
                    <td>{answer}</td>
                </tr>
                );
     });


     return (

             <table>
                <thead>
                    <tr>
                        <th>Question</th>
                        <th>Answer</th>
                    </tr>
                </thead>
                <tbody>{rows}</tbody>
            </table>
            );

  };


export default App
